import React, { useState } from "react";
import axios from 'axios'
import "./FaceRecognition.css";
import Particle from './Particles'
import Lottie from 'lottie-react'
import ticks from './Animations/tick' 


export default function Criminal(){
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [phno, setPhno] = useState("7899819776");
  const [tick, setTick] = useState(false)
  const [status, setStatus] = useState(false)
  const [display, setDisplay] = useState(true)
  const [data, setData] = useState([])
  const handleImageChange = (event) => {
    console.log(event.target.files[0])
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setImage(reader.result);
    };
  };

  const handleUploadClick = () => {
    console.log(image)
    // Add code to handle the upload here
      const obj = {
        Name: name,
        Data: image,
        Phone:phno
      }
      // Add code to handle the upload here
      axios.post('http://localhost:8080/references/add', obj)
        .then((response) => {
          console.log(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    setTick(prev => !prev)
    setDisplay(prev => !prev)
    setTimeout(() => { setTick(prev => !prev) }, 3500)
    console.log('Upload clicked');
    console.log(display)
   };
   console.log(status)
   function handleChange(event) {
    console.log(event.target.value)
    setName(event.target.value)
  }


  return(
    <div>
    {!tick && (<div className="face-recognition2">
      <Particle />
      <input type="text" placeholder="NAME" name="" id="" onChange={handleChange} />
      <div className="upload-container">
        <label htmlFor="image-upload" className="upload-label">
          Select Image
        </label>
        <input
          id="image-upload"

          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />
      </div>
      {image&&display && (
        <div className="image-container">
          <div className="image-preview-container">
            <img src={image} alt="Preview" className="image-preview" />
          </div>
          <button className="upload-button" onClick={handleUploadClick}>
            Upload
   </button>
        </div>
      )}
    </div>)}
    {tick && <div className="tick"><Lottie animationData={ticks} loop={false} /></div>}
    {status&&status.Status &&
      <div className="db-block">
      <div>
      <img className="db-image" src="/images/criminal.jpeg" alt=""/>
      <img className="db-image" src={image} alt=""/>
      </div>

      <div>
      {status.Status?<h2>Found</h2>: <h2>Not Found</h2>}  
      </div>
      </div>}

  </div>
  )
}
