import React, { useState } from "react";
import axios from 'axios'
import "./FaceRecognition.css";
import Particle from './Particles'
import Lottie from 'lottie-react'
import ticks from './Animations/tick'

export default function FaceRecognition() {
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [phno, setPhno] = useState("");
  const [tick, setTick] = useState(false)

  const handleImageChange = (event) => {
    console.log(event.target.files[0])
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setImage(reader.result);
    };
  };

  const handleUploadClick = () => {
    console.log(image)
    const obj = {
      Name: name,
      Data: image
    }
    // Add code to handle the upload here
    axios.post('http://localhost:8080/references/add', obj)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
    setTick(prev => !prev)

    setTimeout(() => { window.location.reload() }, 2000)
    console.log('Upload clicked');

    fetch("http://localhost:5000/run-facial-recognition")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.text();
      })
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
    console.log("Upload clicked");
  };

  function handleChange(event) {
    console.log(event.target.value)
    setName(event.target.value)
  }

  function handleChange2(event) {
    console.log(event.target.value)
    setPhno(event.target.value)
  }


  return (
    <div>
      {!tick && (<div className="face-recognition">
        <Particle />
        <input type="text" placeholder="NAME" name="" id="" onChange={handleChange} />
        <input type="text" placeholder="PHONE" name="" id="" onChange={handleChange2} />
        <div className="upload-container">
          <label htmlFor="image-upload" className="upload-label">
            Select Image
          </label>
          <input
            id="image-upload"

            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>
        {image && (
          <div className="image-container">
            <div className="image-preview-container">
              <img src={image} alt="Preview" className="image-preview" />
            </div>
            <button className="upload-button" onClick={handleUploadClick}>
              Upload
     </button>
          </div>
        )}
      </div>)}
      {tick && <div className="tick"><Lottie animationData={ticks} loop={false} /></div>}

    </div>

  );
}
