import React, { useState } from "react";
import axios from 'axios'
import Particle from './Particles'
import Lottie from 'lottie-react'
import ticks from './Animations/tick'
import "./CriminalDatabase.css";

export default function CriminalDatabase() {
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [phno, setPhno] = useState("");
  const [tick, setTick] = useState(false)

  const handleImageChange = (event) => {
    console.log(event.target.files[0])
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setImage(reader.result);
    };
  };
  const handleUploadClick = () => {
    console.log(image)
    const obj = {
      Name: name,
      Data: image,
      Phone:phno
    }
    axios.post("https://example.com/upload", obj)
      .then(() => {
        setTick(true)
      })
      .catch((err) => {
        console.log(err)
      })
  };

  function handleChange(event) {
    console.log(event.target.value)
    setName(event.target.value)
  }

  function handleChange2(event) {
    console.log(event.target.value)
    setPhno(event.target.value)
  }

  return (
    <div>
      {!tick && (
        <div className="criminal-database">
          <Particle />
          <input type="text" placeholder="NAME" name="" id="" onChange={handleChange} />
          <input type="text" placeholder="PHONE" name="" id="" onChange={handleChange2} />
          <div className="upload-container">
            <label htmlFor="image-upload" className="upload-label">
              Select Image
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
          </div>
          {image && (
            <div className="image-container">
              <div className="image-preview-container">
                <img src={image} alt="Preview" className="image-preview" />
              </div>
              <button className="upload-button" onClick={handleUploadClick}>
                Upload
              </button>
            </div>
          )}
        </div>
      )}

    </div>
  );
}
